mapper.insert:

INSERT INTO DEBT 
	( ID
	  , DNAME
	  , LOCATION
	  , MANAGER_ID )
VALUES ( #{id}
	  , #{dname}
	  , #{location}
	  , #{managerId} )

mapper.update:

UPDATE DEBT
SET ID=#{id}
	, DNAME=#{dname}
	, LOCATION=#{location}
	, MANAGER_ID=#{managerId}

mapper.select:
SELECT * FROM (
	SELECT A.*, ROWNUM RNUM FROM (
				SELECT
								ID
								, DNAME
								, LOCATION
								, MANAGER_ID
						FROM DEBT
				WHERE 1=1

					AND
						ID = #{searchKeyword}

					AND
						DNAME LIKE '%' || #{searchKeyword} || '%'


					ORDER BY 


	) A WHERE ROWNUM <= #{lastIndex}
)WHERE RNUM > #{firstIndex}

SELECT COUNT(*) totcnt FROM DEBT WHERE 1=1
AND ID = #{searchKeyword}
AND DNAME LIKE '%' || #{searchKeyword} || '%'
